INSERT INTO `#__kunena_categories` VALUES (1,0,'Main Forum','main-forum','',0,0,'joomla.group',0,1,1,0,1,1,1,NULL,0,'0000-00-00 00:00:00',0,0,0,0,'This is the main forum section. It serves as a container for categories for your topics.','The section header is used to display additional information about the categories of topics that it contains.','','',0,'lastpost','default',0,0,0,0,0,'',0),(2,1,'Welcome Mat','welcome-mat','',0,0,'joomla.group',0,1,1,0,1,1,1,NULL,0,'0000-00-00 00:00:00',0,0,0,0,'We encourage new members to introduce themselves here. Get to know one another and share your interests.','[b]Welcome to the Kunena forum![/b] \n\n Tell us and our members who you are, what you like and why you became a member of this site. \n We welcome all new members and hope to see you around a lot!','','',0,'lastpost','default',1,1,1,1,1475809323,'',0),(3,1,'Suggestion Box','suggestion-box','',0,0,'joomla.group',0,1,1,0,1,2,1,NULL,0,'0000-00-00 00:00:00',0,0,0,0,'Have some feedback and input to share? \n Don\'t be shy and drop us a note. We want to hear from you and strive to make our site better and more user friendly for our guests and members a like.','This is the optional category header for the Suggestion Box.','','',1,'lastpost','default',0,0,0,0,0,'',0);
